// === PRIORITY 1: EDGE CASES (Check these FIRST to prevent invalid states) ===

// Too many PCs
if (canvasSlots.filter(slot => slot.hasPC()).length > 2) {
  return "‚ùå Only 2 PCs needed - remove the extra one";
}

// Too many routers
if (canvasSlots.filter(slot => slot.hasRouter()).length > 1) {
  return "‚ùå Only 1 router needed - remove the extra one";
}

// Invalid cable: PC to PC
if (connections.some(c => c.from.type === "PC" && c.to.type === "PC")) {
  return "‚ùå PCs can't connect directly - connect them to the router instead";
}

// Invalid cable: Both ends on router (shouldn't happen with 1 router, but just in case)
if (connections.some(c => c.from.type === "Router" && c.to.type === "Router")) {
  return "‚ùå Both cable ends are on the router - connect one end to a PC";
}

// Duplicate cable on same PC
if (hasDuplicateCableOnDevice()) {
  return "‚ùå This PC already has a cable - connect the other PC instead";
}


// === PRIORITY 2: CONFIGURATION ERRORS (Check before showing success) ===

// Invalid IP format
if (routerSettingsOpen && startIP && !isValidIP(startIP)) {
  return "‚ùå Invalid IP - each number must be between 0-255";
}

if (routerSettingsOpen && endIP && !isValidIP(endIP)) {
  return "‚ùå Invalid IP - each number must be between 0-255";
}

// Start > End
if (routerSettingsOpen && startIP && endIP && parseIP(startIP) > parseIP(endIP)) {
  return "‚ùå Start IP must be lower than End IP";
}

// Range too small
if (routerSettingsOpen && startIP && endIP && calculateRange(startIP, endIP) < 2) {
  return "‚ùå Range too small - you need at least 2 addresses for 2 PCs";
}


// === PRIORITY 3: MAIN PROGRESSION (Normal happy path from start to finish) ===

// Stage 1: Empty canvas
if (canvasSlots.every(slot => slot.isEmpty())) {
  return "Drag a PC from inventory to any slot to start";
}

// Stage 2: Only one PC
if (canvasSlots.filter(slot => slot.hasPC()).length === 1 && 
    !canvasSlots.some(slot => slot.hasRouter())) {
  return "Add the second PC to another slot";
}

// Stage 3: Two PCs, no router
if (canvasSlots.filter(slot => slot.hasPC()).length === 2 && 
    !canvasSlots.some(slot => slot.hasRouter())) {
  return "Place the router in the middle slot to connect both PCs";
}

// Stage 4: All devices placed, no cables
if (canvasSlots.filter(slot => slot.hasPC()).length === 2 && 
    canvasSlots.some(slot => slot.hasRouter()) &&
    connections.length === 0) {
  return "Connect PC-1 to the router using a cable";
}

// Stage 5: One cable connected
if (connections.length === 1 && connections[0].isValid()) {
  return "Connect PC-2 to the router with the second cable";
}

// Stage 6: Both cables connected, router not configured
if (connections.length === 2 && 
    connections.every(c => c.isValid()) &&
    !routerSettingsOpen &&
    !dhcpConfigured) {
  return "‚ö†Ô∏è Physically connected but not working! Click the router to configure DHCP";
}

// Stage 7: Router settings open, DHCP disabled
if (routerSettingsOpen && !dhcpEnabled) {
  return "Enable DHCP so the router can assign IP addresses";
}

// Stage 8: DHCP enabled, no range set
if (routerSettingsOpen && dhcpEnabled && (!startIP || !endIP)) {
  return "Set IP range - Start: 192.168.1.100, End: 192.168.1.200";
}

// Stage 9: Range set, not applied
if (routerSettingsOpen && dhcpEnabled && startIP && endIP && !dhcpApplied) {
  return "Click 'Apply' to activate DHCP";
}

// Stage 10: SUCCESS
if (dhcpConfigured && pcsHaveIPs()) {
  return "üéâ Network configured! Both PCs can now communicate";
}


// === PRIORITY 4: HELPFUL HINTS (Optional quality-of-life suggestions) ===

// Router not in center (optional suggestion, not blocking)
if (canvasSlots.some(slot => slot.hasRouter()) && 
    !canvasSlots[2].hasRouter() &&
    connections.length === 0) {
  return "üí° Tip: Put the router in the center slot for easier connections";
}

// User stuck too long
if (timeOnCurrentStage > 30000) {
  return "Need help? Click the '?' button for a detailed walkthrough";
}

// Default fallback
return "";
